export default options => new InSales(options);

/**
 * Output Restler error, used to create Promise reject
 *
 * @param {String} type Required, name of error
 * @param {String} url Required, url where error happen
 * @param {?String} msg Error message
 * @param {?Object} response Response with error data
 * @returns {Object}
 * @property {String} type Name of error
 * @property {String} url Url where error happen
 * @property {?String} msg Error message
 * @property {?Object} response Response with error data
 */
export function error(type, url, msg, response) {
  return {
    type,
    url,
    msg,
    response
  };
}

import { request } from '../request';
import rest from 'restler';
import options from '../options';
const opt = Object.create(options);

/**
 * GET request
 *
 * @param {Object} conn
 * @param {string} conn.id Required, app id
 * @param {string} conn.token Required, token to access shop
 * @param {string} conn.url Required, shop url
 * @param {string} conn.api Required, api endpoint
 * @param {object} [conn.query] URL query string
 * @returns {Promise}
 */
export function get(conn) {
  if (conn.query) opt.query = conn.query;

  const g = rest.get(`https://${conn.id}:${conn.token}@${conn.url}/admin/${conn.api}.xml`, opt);
  return new Promise((resolve, reject) => {
    request(g)
      .then(resolve)
      .catch(reject);
  });
}

import { request } from '../request';
import rest from 'restler';
import { Js2Xml } from 'js2xml';
import options from '../options';
const opt = Object.create(options);

/**
 * POST request
 *
 * @param {Object} conn
 * @param {string} conn.id Required, app id
 * @param {string} conn.token Required, token to access shop
 * @param {string} conn.url Required, shop url
 * @param {string} conn.api Required, api endpoint
 * @param {object} [conn.obj] HTTP POST data
 * @returns {Promise}
 */
export function post(conn) {
  if (conn.obj !== undefined) {
    const js2xml = new Js2Xml(Object.keys(conn.obj)[0], conn.obj[Object.keys(conn.obj)[0]]);
    opt.data = js2xml.toString();
  }

  const p = rest.post(`https://${conn.id}:${conn.token}@${conn.url}/admin/${conn.api}.xml`, opt);
  return new Promise((resolve, reject) => {
    request(p)
      .then(resolve)
      .catch(reject);
  });
}

import { request } from '../request';
import rest from 'restler';
import { Js2Xml } from 'js2xml';
import options from '../options';
const opt = Object.create(options);

/**
 * PUT request
 *
 * @param {Object} conn
 * @param {string} conn.id Required, app id
 * @param {string} conn.token Required, token to access shop
 * @param {string} conn.url Required, shop url
 * @param {string} conn.api Required, api endpoint
 * @param {object} conn.obj HTTP PUT data
 * @returns {Promise}
 */
export function put(conn) {
  if (conn.obj !== undefined) {
    const js2xml = new Js2Xml(Object.keys(conn.obj)[0], conn.obj[Object.keys(conn.obj)[0]]);
    opt.data = js2xml.toString();
  }
  const p = rest.put(`https://${conn.id}:${conn.token}@${conn.url}/admin/${conn.api}.xml`, opt);
  return new Promise((resolve, reject) => {
