}

import { request } from '../request';
import rest from 'restler';
import options from '../options';
const opt = Object.create(options);

/**
 * DELETE request
 *
 * @param {Object} conn
 * @param {string} conn.id Required, app id
 * @param {string} conn.token Required, token to access shop
 * @param {string} conn.url Required, shop url
 * @param {string} conn.api Required, api endpoint
 * @returns {Promise}
 */
export function remove(conn) {
  const d = rest.del(`https://${conn.id}:${conn.token}@${conn.url}/admin/${conn.api}.xml`, opt);
  return new Promise((resolve, reject) => {
    request(d)
      .then(resolve)
      .catch(reject);
  });
}

/**
 * Options for Restler
 *
 * @returns {Object}
 */
export default {
  headers: {
    'Content-Type': 'application/xml'
  },
  xml2js: {
    trim: false,
    explicitArray: false,
    ignoreAttrs: true
  },
  timeout: 30000
};

/**
 * Output Restler response, used to create Promise resolve
 *
 * @param {Object} data Required
 * @param {Object} response Required
 * @returns {Object}
 * @property {Object} data
 * @property {Object} response
 */
export function output(data, response) {
  return {
    data,
    response
  };
}

import { error } from './error';
import { output } from './output';

/**
 * Events of Restler
 *
 * @param {Object} r Required, restler
 * @returns {Promise}
 */
export function request(r) {
  return new Promise((resolve, reject) => {
    r.once('timeout', ms => {
      reject(error('Timeout', r.url.href, `timeout: ${ms}ms`, null));
    }).once('error', (err, response) => {
      reject(error('Error', r.url.href, err, response));
    }).once('abort', () => {
      reject(error('Abort', r.url.href, null, null));
    }).once('fail', (data, response) => {
      reject(error('Fail', r.url.href, data, response));
    }).once('success', (data, response) => {
      resolve(output(data, response));
    });
  });
}
